import logo from "./logo.svg";
import "./App.css";
import { Button } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { useState } from "react";

function App() {
  const [prevTokens, setPrevTokens] = useState([
    "This",
    "app",
    "is",
    "an",
    "awesome",
  ]);
  const [currentTokens, setCurrentTokens] = useState([
    "awesome",
    "lame",
    "great",
    "interesting",
    "stupid",
  ]);

  const pickWord = ({ target }) => {
    const newToken = target.innerHTML;
    setPrevTokens((prev) => {
      return [...prev, newToken];
    });
    fetch("https://api.sampleapis.com/switch/games")
      .then((response) => response.json())
      .then((data) => console.log(data))
      .catch((error) => console.error(error));
  };

  const deleteLast = () => {
    setPrevTokens((prev) => {
      return prev.slice(0, -1);
    });
  };

  return (
    <div className="App">
      <h1> LLM Brain </h1>
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <div style={{ display: "flex", flexDirection: "row" }}>
          <Button color="light" onClick={deleteLast}>
            {" "}
            Delete Last Word{" "}
          </Button>
        </div>

        <div
          style={{
            width: "100vw",
            height: "5vh",
            display: "flex",
            flexDirection: "row",
          }}
        >
          {prevTokens.map((token, index) => (
            <div style={{ display: "flex", flexDirection: "column" }}>
              <p style={{ marginRight: "2vw" }}> {index + 1} </p>
              <Button color="danger" style={{ marginRight: "2vw" }}>
                {" "}
                {token}{" "}
              </Button>
            </div>
          ))}
          <div
            style={{
              height: "50vh",
              flexDirection: "column",
              display: "flex",
              justifyContent: "space-between",
            }}
          >
            <p> Next Word </p>
            {currentTokens.map((token) => (
              <Button color="success" onClick={pickWord}>
                {token}
              </Button>
            ))}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
