import React, { useState } from "react";
import Modal from "react-modal";
import { Button } from "reactstrap";

function ModalComponent() {
  const [modalIsOpen, setModalIsOpen] = useState(true);

  const setModalIsOpenToTrue = () => {
    setModalIsOpen(true);
  };

  const setModalIsOpenToFalse = () => {
    setModalIsOpen(false);
  };

  const customStyles = {
    content: {
      top: "50%",
      left: "50%",
      right: "auto",
      bottom: "auto",
      marginRight: "-50%",
      transform: "translate(-50%, -50%)",
      backgroundColor: "#F0AA89",
      borderRadius: "20px",
    },
  };

  return (
    <>
      <Button onClick={setModalIsOpenToTrue}> Instructions </Button>

      <Modal isOpen={modalIsOpen} style={customStyles}>
        <Button style={{ float: "right" }} onClick={setModalIsOpenToFalse}>
          x
        </Button>
        <p>
          {" "}
          Welcome to LLMBrain! This app lets you play with LLM Models and
          observe how they think behind the scenes.
        </p>
        <Button color="success"> Next </Button>
      </Modal>
    </>
  );
}
export default ModalComponent;
