import logo from "./logo.svg";
import "./App.css";
import { Button } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { useEffect, useState } from "react";
import IntroModal from "./IntroModal";

function App() {
  const [prevTokens, setPrevTokens] = useState([
    " The",
    " winners",
    " of",
    " this",
    " year's",
  ]);
  const [candidates, setCandidates] = useState({ tokens: [], values: [] });

  // useEffect is called when prevTokens changes, and also at the first render.
  useEffect(() => {
    console.log("after new word:" + prevTokens);
    console.log("probs", candidates.values);
    fetch("http://0.0.0.0:8002", {
      method: "POST",
      body: JSON.stringify({
        text: prevTokens.join(""),
      }),
      headers: {
        "Content-Type": "application/json; charset=UTF-8",
      },
    })
      .then((response) => response.json())
      .then((response) =>
        setCandidates((prev) => {
          return response;
        })
      )
      .catch((error) => console.error(error));
  }, [prevTokens]);

  const pickWord = ({ target }) => {
    const newToken = target.innerHTML;
    setPrevTokens((prev) => {
      return [...prev, newToken];
    });
  };

  const deleteLast = () => {
    setPrevTokens((prev) => {
      return prev.slice(0, -1);
    });
  };

  return (
    <div className="App">
      <h1> LLM Brain </h1>

      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <div style={{ display: "flex", flexDirection: "row" }}>
          <Button color="light" onClick={deleteLast}>
            {" "}
            Delete Last Word <IntroModal> </IntroModal>
          </Button>
        </div>

        <div
          style={{
            width: "100vw",
            height: "5vh",
            display: "flex",
            flexDirection: "row",
          }}
        >
          {prevTokens.map((token, index) => (
            <div style={{ display: "flex", flexDirection: "column" }}>
              <p style={{ marginRight: "2vw" }}> {index + 1} </p>
              <Button color="danger" style={{ marginRight: "1vw" }}>
                {" "}
                {token}{" "}
              </Button>
            </div>
          ))}
          <div
            style={{
              height: "50vh",
              flexDirection: "column",
              display: "flex",
              justifyContent: "space-between",
              marginRight: "1vh",
            }}
          >
            <p> Next Word </p>
            {candidates.tokens.map((token) => (
              <Button color="success" onClick={pickWord}>
                {token}
              </Button>
            ))}
          </div>

          <div
            style={{
              height: "50vh",
              flexDirection: "column",
              display: "flex",
              justifyContent: "space-between",
            }}
          >
            <p> Probability </p>
            {candidates.values.map((value) => (
              <Button color="info">
                {(Math.round(value * 10000) / 100).toString() + "%"}
              </Button>
            ))}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
