import React, { useState } from "react";
import Modal from "react-modal";
import { Button } from "reactstrap";

function ModalComponent() {
  const windowTextList = [
    "Welcome to LLMBrain! This app lets you play with Large Language Models and observe how they think behind the scenes.",
    `The main objective of generative LLMs such as ChatGPT, is to determine what the next word should be for a given sequence of words.
                     Behind the scenes, the model calculates the probability of each word in the corpus being the next one.`,
    `The corpus consists of all the words that the model has seen during its learning phase. More than 50,000 words in a corpus is pretty common.`,
  ];
  const [modalIsOpen, setModalIsOpen] = useState(true);
  const [windowTextId, setWindowTextId] = useState(0);

  const setModalIsOpenToTrue = () => {
    setModalIsOpen(true);
  };

  const setModalIsOpenToFalse = () => {
    setWindowTextId(() => 0);
    setModalIsOpen(false);
  };

  const customStyles = {
    content: {
      height: "30vh",
      width: "40vw",
      marginTop: "30vh",
      marginBottom: "25vh",
      marginRight: "25vw",
      marginLeft: "45vw",
      backgroundColor: "orange",
      borderRadius: "20px",
      display: "flex",
      justifyContent: "center",
      flexDirection: "column",
    },
  };

  return (
    <div>
      <Button onClick={setModalIsOpenToTrue}> Instructions </Button>

      <Modal isOpen={modalIsOpen} style={customStyles}>
        <Button
          color="danger"
          style={{
            height: "4vh",
            width: "4vh",
            alignSelf: "end",
            justifySelf: "start",
          }}
          onClick={setModalIsOpenToFalse}
        >
          x
        </Button>
        <p style={{ marginTop: "5vh" }}> {windowTextList[windowTextId]} </p>
        <Button
          color="success"
          onClick={() => setWindowTextId((prev) => prev + 1)}
        >
          {" "}
          Next{" "}
        </Button>
      </Modal>
    </div>
  );
}
export default ModalComponent;
